mapper.select:
SELECT tran_no, prod_no, buyer_id, payment_option, receiver_name, receiver_phone, demailaddr, DLVY_REQUEST, tran_status_code, order_data
FROM transaction
WHERE tran_no = #{value}

mapper.update:
UPDATE transaction
SET tran_status_code = 2
WHERE tran_no = #{value}

